// src/pages/Services.js
import React from 'react';
import Button from '../components/Button'; // Adjust the import path as necessary

const Services = () => {
  const services = [
    {
      title: 'Residential Solar',
      description: 'Custom solar solutions for your home to reduce energy bills and carbon footprint.',
      icon: 'üè†',
    },
    {
      title: 'Commercial Solar',
      description: 'Scalable solar systems for businesses to cut operational costs and demonstrate sustainability.',
      icon: 'üè¢',
    },
    {
      title: 'Solar Maintenance',
      description: 'Regular maintenance and cleaning services to keep your system running at peak efficiency.',
      icon: 'üîß',
    },
    {
      title: 'Energy Storage',
      description: 'Battery solutions to store excess energy for use during peak hours or outages.',
      icon: 'üîã',
    },
    {
      title: 'Consultation',
      description: 'Expert advice to help you choose the right solar solution for your needs.',
      icon: 'üí°',
    },
    {
      title: 'Government Incentives',
      description: 'We help you navigate and apply for available solar incentives and rebates.',
      icon: 'üìë',
    },
  ];

  return (
    <div className="py-16 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-solar.text sm:text-4xl">
            Our Solar Services
          </h2>
          <p className="mt-4 text-xl text-gray-600">
            Comprehensive solutions for all your solar energy needs
          </p>
        </div>

        <div className="mt-16 grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
          {services.map((service, index) => (
            <div
              key={index}
              className="pt-10 pb-8 px-6 border border-gray-200 rounded-2xl text-center hover:shadow-lg transition-shadow duration-300"
            >
              <div className="w-20 h-20 mx-auto flex items-center justify-center bg-solar.light rounded-full text-3xl mb-6">
                {service.icon}
              </div>
              <h3 className="text-xl font-bold text-solar.text mb-3">{service.title}</h3>
              <p className="text-gray-600">{service.description}</p>
              <Button className="mt-6 bg-solar.primary text-white hover:bg-solar.dark">
                Learn More
              </Button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Services;
