// src/components/Hero.js
import React from 'react';
import Button from './Button';

const Hero = () => {
  return (
    <div className="relative bg-gradient-to-br from-solar.primary to-solar.dark text-white py-20 px-4">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-6">Powering Your Future with Solar Energy</h1>
        <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto">
          Sustainable, affordable solar solutions for homes and businesses. Save money while saving the planet.
        </p>
        <div className="flex flex-col sm:flex-row justify-center gap-4">
          <Button className="bg-white text-solar.text hover:bg-gray-100 px-8 py-3 text-lg">
            Get Started
          </Button>
          <Button className="bg-transparent border-2 border-white hover:bg-white hover:text-solar.text px-8 py-3 text-lg">
            Learn More
          </Button>
        </div>
      </div>
      <div className="absolute bottom-0 left-0 right-0 h-16 bg-white transform skew-y-2 origin-bottom-left"></div>
    </div>
  );
};

export default Hero;
