import React from 'react';
import Button from './Button';
import solarPanels from '../assets/solarpanels.jpg';

const Hero = () => {
  return (
    <div 
      className="relative py-20 px-4 text-white"
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), url(${solarpanels})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-4xl md:text-6xl font-bold mb-6">Powering Your Future with Solar Energy</h1>
        {/* ... rest of your Hero component */
