// src/pages/Pricing.js
import React from 'react';
import Button from '../components/Button'; 

const Pricing = () => {
  const plans = [
    {
      name: 'Basic',
      price: '$2,999',
      features: [
        '5kW Solar Panel System',
        'Standard Installation',
        '10-Year Warranty',
        'Basic Monitoring',
      ],
      recommended: false,
    },
    {
      name: 'Premium',
      price: '$4,999',
      features: [
        '8kW Solar Panel System',
        'Premium Installation',
        '15-Year Warranty',
        'Smart Monitoring',
        'Energy Storage Option',
      ],
      recommended: true,
    },
    {
      name: 'Enterprise',
      price: 'Custom',
      features: [
        '10kW+ Solar Panel System',
        'Custom Installation',
        '20-Year Warranty',
        'Advanced Monitoring',
        'Energy Storage Included',
        'Priority Support',
      ],
      recommended: false,
    },
  ];

  return (
    <div className="py-16 bg-solar.background">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center">
          <h2 className="text-3xl font-extrabold text-solar.text sm:text-4xl">
            Simple, transparent pricing
          </h2>
          <p className="mt-4 text-xl text-gray-600">
            No surprises. Everything you need to harness solar energy.
          </p>
        </div>

        <div className="mt-16 space-y-8 lg:space-y-0 lg:grid lg:grid-cols-3 lg:gap-x-8">
          {plans.map((plan) => (
            <div
              key={plan.name}
              className={`relative p-8 border rounded-2xl shadow-sm ${plan.recommended ? 'border-2 border-solar.accent' : 'border-gray-200'} bg-white`}
            >
              {plan.recommended && (
                <div className="absolute top-0 right-0 -mt-3 -mr-3 px-3 py-1 bg-solar.accent text-white text-sm font-semibold rounded-full">
                  Recommended
                </div>
              )}
              <div className="mb-6">
                <h3 className="text-lg font-medium text-solar.text">{plan.name}</h3>
                <p className="mt-4 flex items-baseline">
                  <span className="text-5xl font-extrabold text-solar.text">{plan.price}</span>
                  {plan.price !== 'Custom' && (
                    <span className="ml-1 text-xl font-semibold text-gray-500">/system</span>
                  )}
                </p>
                <p className="mt-4 text-gray-500">Perfect for {plan.name.toLowerCase()} needs</p>
              </div>
              <ul className="space-y-3">
                {plan.features.map((feature) => (
                  <li key={feature} className="flex items-start">
                    <svg
                      className="h-6 w-6 flex-shrink-0 text-solar.primary"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M5 13l4 4L19 7"
                      />
                    </svg>
                    <span className="ml-3 text-gray-700">{feature}</span>
                  </li>
                ))}
              </ul>
              <div className="mt-8">
                <Button
                  className={`w-full ${plan.recommended ? 'bg-solar.accent hover:bg-solar.dark text-white' : 'bg-gray-100 hover:bg-gray-200 text-solar.text'}`}
                >
                  Get {plan.name}
                </Button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Pricing;
