// src/components/LiveChat.jsx
import React, { useState } from 'react';
import { FiMessageSquare, FiX } from 'react-icons/fi';

const LiveChat = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      // Mock response
      setTimeout(() => {
        setMessages((prev) => [
          ...prev,
          { text: 'Thanks for your message! How can we assist you with solar solutions?', sender: 'bot' }
        ]);
      }, 1000);
      setInput('');
    }
  };

  return (
    <>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-8 left-8 z-50 flex items-center gap-2 bg-gradient-to-r from-yellow-400 to-orange-500 text-white font-bold py-3 px-4 rounded-full shadow-xl hover:shadow-2xl transition-all duration-300"
        aria-label={isOpen ? 'Close chat' : 'Open live chat'}
      >
        {isOpen ? <FiX className="text-xl" /> : <FiMessageSquare className="text-xl" />}
        {isOpen ? 'Close' : 'Chat'}
      </button>
      {isOpen && (
        <div className="fixed bottom-20 left-8 w-80 bg-white dark:bg-gray-800 rounded-lg shadow-xl z-50 p-4">
          <div className="h-64 overflow-y-auto mb-4">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`mb-2 ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}
              >
                <span
                  className={`inline-block p-2 rounded-lg ${
                    msg.sender === 'user'
                      ? 'bg-yellow-500 text-white'
                      : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-300'
                  }`}
                >
                  {msg.text}
                </span>
              </div>
            ))}
          </div>
          <form onSubmit={handleSubmit} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              className="flex-1 px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-yellow-500 dark:bg-gray-700 dark:text-white"
              placeholder="Type a message..."
              aria-label="Chat message input"
            />
            <button
              type="submit"
              className="bg-yellow-500 text-white hover:bg-yellow-600 px-3 py-2 rounded-lg"
              aria-label="Send message"
            >
              Send
            </button>
          </form>
        </div>
      )}
    </>
  );
};

export default LiveChat;
