import React, { useState, useEffect, useRef } from 'react';

// ... (keep all your existing imports)

const Contact = () => {
  // ... (keep all your existing state)

  // Chatbot specific state
  const [chatbotOpen, setChatbotOpen] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    { sender: 'bot', text: 'Hello! How can I help you with solar solutions today?' },
  ]);
  const [userMessage, setUserMessage] = useState('');
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom of chat
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const handleChatbotToggle = () => {
    setChatbotOpen(!chatbotOpen);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (userMessage.trim() === '') return;

    // Add user message
    const newUserMessage = { sender: 'user', text: userMessage };
    setChatMessages([...chatMessages, newUserMessage]);
    setUserMessage('');

    // Simulate bot response after delay
    setTimeout(() => {
      const botResponse = getBotResponse(userMessage.toLowerCase());
      setChatMessages(prev => [...prev, { sender: 'bot', text: botResponse }]);
    }, 1000);
  };

  const getBotResponse = (message) => {
    if (message.includes('cost') || message.includes('price')) {
      return "Our residential solar systems typically range from â‚¹1.5-2.5 lakhs after subsidies. Would you like a free customized quote?";
    } else if (message.includes('option') || message.includes('panel')) {
      return "We offer monocrystalline (high efficiency) and polycrystalline (budget-friendly) panels. Both come with 25-year performance warranties.";
    } else if (message.includes('financ') || message.includes('loan')) {
      return "We provide multiple options: 1) Outright purchase 2) Solar loans (EMI options) 3) Lease agreements. Which would you like to know more about?";
    } else if (message.includes('contact') || message.includes('reach')) {
      return "You can call us at +91 98765 43210 or email support@unnati-renewables.com. Our team is available Mon-Sat 9AM-6PM.";
    } else if (message.includes('thank') || message.includes('thanks')) {
      return "You're welcome! Is there anything else I can help you with?";
    } else {
      return "I can help with pricing, panel options, financing, and more. Please ask your question or select from the quick options below.";
    }
  };

  const handleQuickQuestion = (question) => {
    setUserMessage(question);
  };

  // ... (keep all your existing code until the chatbot section)

  {/* Enhanced Chatbot */}
  <div className={`fixed bottom-8 right-8 transition-all duration-300 ${chatbotOpen ? 'h-96 w-80' : 'h-16 w-16'}`}>
    <div className="bg-white rounded-xl shadow-xl overflow-hidden border border-gray-200 h-full flex flex-col">
      {chatbotOpen ? (
        <>
          <div className="bg-yellow-500 p-4 flex justify-between items-center">
            <h3 className="text-lg font-medium text-white">Solar Assistant</h3>
            <button 
              onClick={handleChatbotToggle}
              className="text-white hover:text-gray-200"
            >
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
          
          <div className="flex-1 p-4 overflow-y-auto space-y-3">
            {chatMessages.map((msg, index) => (
              <div 
                key={index} 
                className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div 
                  className={`rounded-lg p-3 max-w-xs ${msg.sender === 'user' 
                    ? 'bg-yellow-500 text-white' 
                    : 'bg-gray-100 text-gray-800'}`}
                >
                  <p className="text-sm">{msg.text}</p>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          
          <div className="p-3 bg-gray-50 border-t border-gray-200">
            <div className="flex flex-wrap gap-2 mb-2">
              <button 
                onClick={() => handleQuickQuestion('What are your solar panel options?')}
                className="text-xs bg-gray-200 hover:bg-gray-300 rounded-full px-3 py-1"
              >
                Panel Options
              </button>
              <button 
                onClick={() => handleQuickQuestion('How much does solar installation cost?')}
                className="text-xs bg-gray-200 hover:bg-gray-300 rounded-full px-3 py-1"
              >
                Pricing
              </button>
              <button 
                onClick={() => handleQuickQuestion('What financing options do you offer?')}
                className="text-xs bg-gray-200 hover:bg-gray-300 rounded-full px-3 py-1"
              >
                Financing
              </button>
            </div>
            
            <form onSubmit={handleSendMessage} className="flex">
              <input
                type="text"
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1 border border-gray-300 rounded-l-lg px-3 py-2 text-sm focus:outline-none focus:ring-1 focus:ring-yellow-500"
              />
              <button 
                type="submit"
                className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-2 rounded-r-lg text-sm"
              >
                Send
              </button>
            </form>
          </div>
        </>
      ) : (
        <button
          onClick={handleChatbotToggle}
          className="h-full w-full flex items-center justify-center bg-yellow-500 hover:bg-yellow-600 text-white rounded-xl relative"
        >
          <FiMessageSquare className="h-6 w-6" />
          {chatMessages.length > 1 && (
            <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center transform translate-x-1 -translate-y-1">
              {chatMessages.length - 1}
            </span>
          )}
        </button>
      )}
    </div>
  </div>

  // ... (keep the rest of your existing code)
};

export default Contact;
